$(function() {
    $('.hidden_div').hide();
    $('#type').change(function(){
        $('.row-graduacao')[ ($("option[value='0']").is(":checked"))? "show" : "hide" ]();
        $('.row-residencia')[ ($("option[value='1']").is(":checked"))? "show" : "hide" ]();
        $('.row-residencia-multi')[ ($("option[value='2']").is(":checked"))? "show" : "hide" ]();
        $('.row-internato')[ ($("option[value='internato']").is(":checked"))? "show" : "hide" ]();
    });
    $('#type1').change(function(){
        $('.row-internato')[ ($("option[value='internato']").is(":checked"))? "show" : "hide" ]();
    });
});
